<?PHPrequire_once("./include/membersite_config.php");if(!$fgmembersite->CheckLogin()){    $fgmembersite->RedirectToURL("login.php");    exit;}?><html><head>    <meta charset='UTF-8'>    <title>Request a Cup</title>    <link href='../stylesheet1.css' rel='stylesheet'>    <link href='insertneed.php'>    <link href='https://fonts.googleapis.com/css?family=Quicksand' rel='stylesheet' type='text/css'>    <script type="text/javascript">        // function to show/hide div element        function toggleDiv(divID) {            var myDiv = document.getElementById(divID);                        if(myDiv.style.display == 'none')                myDiv.style.display = 'block';            else                myDiv.style.display = 'none';        }        // show and hide divs based on radio selection        function hideGoods() {            document.getElementById("goodsBlock").style.visibility = "hidden";            document.getElementById("servicesBlock").style.visibility = "visible";        }        function hideServices() {            document.getElementById("servicesBlock").style.visibility = "hidden";            document.getElementById("goodsBlock").style.visibility = "visible";        }    </script></head>  <body>    <div id='navBar'>      <ul>        <a href='http://www.codex209.com/~prodapp/home.html'>            <li style='padding-right: 40px;'><img src='../cupOfSugarLogo1.jpg' /></li>        </a>        <li><a href='http://www.codex209.com/~prodapp/source/give.php'>Give</a></li>        <li><a href='http://www.codex209.com/~prodapp/source/orgPage.php'>Your Requests</a></li>        <li><a href='http://www.codex209.com/~prodapp/source/logout.php'>Log-out</a></li>      </ul>    </div>    <div class='floatingBox' style='max-width=5%'>        <p>Organization: <?= $fgmembersite->UserFullName() ?></p>        <p>Email: <?= $fgmembersite->UserEmail() ?></p>    </div>    <div class='afterBox'>        <h3>Hello <?= $fgmembersite->UserFullName() ?></h3>        <button onclick="toggleDiv('requestBlock');">Get some sugar</button>        <button onclick="toggleDiv('giveBlock');">My Requests</button>        <br /><br />        <div class="boxContent" id='requestBlock' style="display: none;">          <form id="needForm" name="needForm" action="insertneed.php" method="post">            <label for="goodsOrServices">Are you in need of goods or services?</label>            <input name="goodsOrServices" type="radio" onclick="hideServices()" value="Goods" checked>Goods            <input name="goodsOrServices" type="radio" onclick="hideGoods()" value="Services">Services            <br /><br />            <div id='goodsBlock' style="position: absolute;">              <label for="goodsType">Type of Goods</label>              <br/>              <select name="goodsType" id="goodsType" required>                <option value="food">Food</option>                <option value="clothing">Clothing</option>                <option value="shoes">Shoes</option>                <option value="bedding">Bedding</option>                <option value="furniture">Furniture</option>                <option value="appliance">Appliance</option>                <option value="vehicle">Vehicle</option>                <option value="other">Other</option>              </select>            </div>            <div id='servicesBlock' style="position: absolute; visibility: hidden;">            <label for="servicesType">Type of Service</label>              <select name="servicesType" id="servicesType" required>                <option value="education">Education</option>                <option value="medical services">Medical Services</option>                <option value="transportation">Transportation</option>                <option value="labor">Labor</option>                <option value="child care">Child Care</option>               <option value="other">Other</option>              </select>            </div>            <br /><br /><br /><br />            <br/>            <label for="needQuantity">Quantity or Number of Volunteers</label>            <input id="needQuantity" name="needQuantity" type="number" required>            <br />            <label for="needDate">Date Needed</label>            <input id="needDate" name="needDate" type="date" required>            <br />            <label for="needTime">Time Needed (optional)</label>            <input id="needTime" name="needTime" type="text">            <br />            <label for="needInfo">Additional Information (optional)</label>            <input id="needInfo" name="needInfo" type="text" maxlength="250" value="Please describe in 250 characters or less...">            <br />            <label for="needContactName">Contact Name</label>            <input id="needContactName" name="needContactName" type="text" required>            <br />            <label for="needContactEmail">Contact Email</label>            <input id="needContactEmail" name="needContactEmail" type="text" required>            <br />            <input type="hidden" name="needOrg" value="<?= $fgmembersite->UserFullName() ?>">            <input type="submit" name="submitNeedForm" value="Submit">          </form>        </div>        <div class="boxContent" id='giveBlock' style="display: none;">          <style>label.thick {font-weight: bold;} form {box-shadow: 10px 10px 5px #888888; padding: 5px;           margin-right:25px; margin-left:25px;} div {margin-top:10px;}          h1 {color: #ff7f50;text-align: center; padding-left: 25px; padding-right: 20px; padding-top: 5px;}           </style><?php    $servername = $fgmembersite->GetDBHost();    $username = $fgmembersite->GetDBUser();    $password = $fgmembersite->GetDBPass();    $dbname = $fgmembersite->GetDB();    $pullOrg = $fgmembersite->UserFullName();        // Create connection    $conn = new mysqli($servername, $username, $password, $dbname);        // Check connection    if ($conn->connect_error) {        die('Connection failed: ' . $conn->connect_error);    }     //default pull for initial load of the page. will re-pull as clear/submit buttons are used based on filter criteria    $sql = "SELECT needs.id, needs.organization_name, needs.needtype, needs.needsubtype, needs.description, needs.deadline, needs.starttime,               needs.numhours, needs.contact_firstname, needs.contact_lastname, needs.phone, needs.email, fgusers3.name, fgusers3.orgDescription, fgusers3.contactName, fgusers3.phoneNumber,              fgusers3.email, fgusers3.address1, fgusers3.address2, fgusers3.orgCity, fgusers3.orgState, fgusers3.orgZipCode, fgusers3.username              FROM needs INNER JOIN fgusers3  ON needs.organization_name=fgusers3.name               AND fgusers3.name = '$pullOrg'              ORDER BY id DESC";      $result = $conn->query($sql);    if ($result->num_rows > 0) {    // output data of each row - Displays each current request in their own form box, with options to click buttons to see    //more detail      while($row = $result->fetch_assoc()) {        echo "<form name='".$row["needs.id"]."needresults'>          <label class='thick' name='".$row["needs.id"]."orgName'>Organization Name: ".$row["organization_name"]."</label>          <br />          <label name='".$row["needs.id"]."needtype'>Request Type: ".$row["needtype"]."</label>          <label name='".$row["needs.id"]."needsupbtype'> / ".$row["needsubtype"]."</label>          <br />          <label name='".$row["needs.id"]."deadline'>Date / Deadline: ".$row["deadline"]."</label>          <br />          <label name='".$row["needs.id"]."description'>Details: ".$row["description"]."</label>          <br />          <label name='".$row["needs.id"]."firstname'>Contact: ".$row["contact_firstname"]."</label>          <label name='".$row["needs.id"]."lastname'> ".$row["contact_lastname"]."</label>          <br />          <label name='".$row["needs.id"]."email'>E-Mail: ".$row["email"]."</label>          <label name='".$row["needs.id"]."phone'>     Phone: ".$row["phone"]."</label>        </form>";      }    } else {        echo "0 results";    }    echo "<p2>**All listed requests are Demonstration Data only</p2>";    $conn->close();?>        </div>      </div>  </body></html>